usage_example:
Badge({ count: 10, maxCount: 99, position: BadgePosition.RightTop }) {
  Image($r('app.media.message'))
    .width(48)
    .height(48)
}

Tabs({ barPosition: BarPosition.Start }) {
  TabContent() { Text('首页') }.tabBar('首页')
  TabContent() { Text('我的') }.tabBar('我的')
}
.barWidth('100%')
.barHeight(56)

Navigation() {
  // 页面内容
}
.title('页面标题')
.mode(NavigationMode.Stack)
.titleMode(NavigationTitleMode.Mini)

List({ space: 12 }) {
  ForEach(this.dataList, (item) => {
    ListItem() {
      Text(item.title)
    }
  })
}
.width('100%')
.divider({ strokeWidth: 0.5, color: '#E5E8EB' })

AlertDialog.show({
  title: '提示',
  message: '确认删除此项？',
  primaryButton: {
    value: '取消',
    action: () => { }
  },
  secondaryButton: {
    value: '确认',
    fontColor: '#E84026',
    action: () => { }
  }
})
